import React, { useContext } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { UserContext } from '../context/UserContext'
import './Navbar.css'

function Navbar() {
  const { user, setUser } = useContext(UserContext)
  const navigate = useNavigate()

  const logout = () => {
    setUser(null)
    localStorage.removeItem('token')
    navigate('/')
  }

  return (
    <nav className='navbar navbar-expand-lg navbar-dark bg-dark px-4'>
      <Link className='navbar-brand' to='/'>BookReview</Link>
      <div className='collapse navbar-collapse'>
        <ul className='navbar-nav ms-auto'>
          <li className='nav-item'><Link className='nav-link' to='/books'>Books</Link></li>
          {user ? (
            <>
              <li className='nav-item'><Link className='nav-link' to='/profile'>Profile</Link></li>
              <li className='nav-item'><span className='nav-link' onClick={logout} style={{ cursor: 'pointer' }}>Logout</span></li>
            </>
          ) : (
            <>
              <li className='nav-item'><Link className='nav-link' to='/login'>Login</Link></li>
              <li className='nav-item'><Link className='nav-link' to='/register'>Register</Link></li>
            </>
          )}
        </ul>
      </div>
    </nav>
  )
}

export default Navbar
